mapper.select:
select material_id, material_name, material_num, create_time, create_user,update_time
from material
where material_id = #{materialId,jdbcType=VARCHAR}

select material_id, material_name, material_num, material_description, create_time, create_user, update_time from material where
material_name = #{materialName}
material_name = #{materialName} and material_id !=#{materialId}
